properties.css:

.skin-hidden-text.percent{
 display: block;
 position: absolute;
 right: 8%;
 bottom: 1%;
 font-size: 2.5rem;
}
.skin-hidden-text.percent:after {
 content: "%";
 font-weight: normal;
 font-family: 'Montserrat-Medium';
}

.skin-text.completion-count.sup-zero{ 
display: block !important;
background: #1d1061;
color: white !important;
border-radius: 100%;
position: absolute;
font-size: 23px !important;
width: 39px;
padding: 2px 2px;
text-align: center;
} 
.completion-incomplete{
top: 31%;
left: 55%;
}
.completion-complete{
bottom: 13%;
left: 55%;
}
.completion-fail{
bottom: 14%;
left: 25%;
}
.completion-perfect{
right: 6%;
bottom: 14%;
}
.skin-text.completion-count.sup-zero.perfect{
right: 24%;
font-size: 40px !important;
width: 65px;
}


.skin-hidden-text.percent{
 display: block;
 position: absolute;
 right: 8%;
 bottom: 1%;
 font-size: 4.5rem;
}
.skin-hidden-text.percent:after {
 content: "%";
 font-weight: normal;
 font-family: 'Montserrat-Medium';
}
