css:
.skin-text.completion-count.sup-zero{ 
display: block !important;
background: #1d1061;
color: white !important;
border-radius: 100%;
position: absolute;
font-size: 150% !important;
width: 8%;
padding: 2px 2px;
text-align: center;
} 
.completion-incomplete{
top: 31%;
left: 55%;
}
.completion-complete{
bottom: 13%;
left: 55%;
}
.completion-fail{
bottom: 14%;
left: 25%;
}
.completion-perfect{
right: 6%;
bottom: 14%;
}
.skin-text.completion-count.sup-zero.perfect{
right: 24%;
font-size: 250% !important;
width: 65px;
}
